document.getElementById("form-sorteador").addEventListener("submit",function(e){e.preventDefault();var e=parseInt(document.getElementById("numero-maximo").value,10);isNaN(e)||e<2?alert("Informe um número válido maior ou igual a 2"):(e=Math.floor(Math.random()*e)+1,document.getElementById("resultado-valor").textContent=e,document.querySelector(".resultado").style.display="block")});